from __future__ import annotations


class DLH5Error(Exception):
    pass


class CompatibilityError(DLH5Error):
    pass


class InvalidIndexError(DLH5Error):
    pass


class FileNotOpenError(DLH5Error):
    pass


class UnsupportedDataTypeError(DLH5Error):
    pass


class ChannelNotFoundError(DLH5Error):
    pass


class DatasetExistsError(DLH5Error):
    pass


class MetadataExistsError(DLH5Error):
    pass


class AccessPermissionError(DLH5Error, PermissionError):
    pass


class NoValidIdentifierError(DLH5Error):
    pass


class BrokenExternalLinkError(DLH5Error):
    pass
