from __future__ import annotations

import numpy as np


class DummyLock:
    def acquire(self, timeout=None, poll_intervall=0.05):
        pass

    def release(self, force=False):
        return None


class Waveform:
    """
    This class mocks the PyVerify Waveform class for the DLH5 API to return waveform data even
    if PyVerify is not installed on the system.
    """

    def __init__(self, data, time, **kwargs):
        self.data: np.ndarray = np.array(data)
        self.time: np.ndarray = np.array(time)
