from __future__ import annotations

from monocle_telemetry.api.feature import track_feature as _track_feature
from monocle_telemetry.api.metric import track_metric as _track_metric

from .version import __version_tuple__


def track_feature(feature: str, misc: str = ""):
    _track_feature(
        project="pyveco",
        tool="ifx-dlh5",
        version=".".join(map(str, __version_tuple__[:3])),
        feature=f"dlh5_{feature}",
        misc=misc,
    )


def track_metric(metric: str, value: float):
    _track_metric(
        project="pyveco",
        metric=metric,
        value=value,
    )
