from __future__ import annotations

import logging
import sys

log = logging.getLogger("passfinder")


def add_streamhandler(level=logging.INFO):
    log.setLevel(level)
    sh = logging.StreamHandler(sys.stdout)
    sh.setFormatter(logging.Formatter("%(asctime)s - %(levelname)s - %(message)s"))
    if sh not in log.handlers:
        log.addHandler(sh)
