from __future__ import annotations

from enum import IntEnum


class Enums:
    class ExecutionMode:
        """
        Container for all existing execution modes
        """

        Sim = "SIM"
        Sim_Reload = "SIM_RELOAD"
        Lab = "LAB"
        PostProcOnly = "POSTPROCONLY"
        Undef = "UNDEF"

    class TestVariationStatus:
        Ok = "Ok"
        Fail = "Fail"

    class TestStatus:
        Idle = "Idle"
        Paused = "Paused"
        Busy = "Busy"
        Done = "Done"
        Error = "Error"

    class Simulator:
        Simetrix = "Simetrix"
        IFXspice = "IFXspice"
        AMSDesigner = "AMSDesigner"

    class ProcessingMode:
        Local = "Local"
        Distributed = "Distributed"

    class SimulatorSignals:
        NONE = None
        ALL = "ALL"
        TOPLEVEL = "TOPLEVEL"

    class InvokeTime(IntEnum):
        BeforeVarLoop = 0
        AfterVarLoop = 1
        BeforeVarLoopPP = 2
        AfterVarLoopPP = 3

    SimExecutionModes = [ExecutionMode.Sim, ExecutionMode.Sim_Reload]
