from __future__ import annotations

from dlh5 import DLH5

from .converters.dlh5_to_json import dlh5_to_json
from .converters.dlh5_to_junitxml import dlh5_to_junitxml
from .converters.hdf5_to_dlh5 import hdf5_to_dlh5


class PyVerifyDLH5(DLH5):
    """
    DLH5 extensions specific to PyVerify
    """

    convert_to_json = staticmethod(dlh5_to_json)
    convert_to_junitxml = staticmethod(dlh5_to_junitxml)
    convert_from_hdf5 = staticmethod(hdf5_to_dlh5)
