from __future__ import annotations


class Instruments:
    """Common container for all required and connected instruments.
    If any of these instruments are not available an error will be raised.
    Please assign 'None' to instruments that are not connected/switched on."""

    def __init__(self):
        self.ACPowerSupply = None  # ACPowerSupply()
        """:type : ACPowerSupply"""
        self.ProgLoad = None  # ProgLoad()
        """:type : ProgLoad"""
        self.PowerAnalyzer = None  # PowerAnalyzer()
        """:type : PowerAnalyzer"""
        self.Scope = None  # Scope_1()
        """:type : Scope_1"""
        self.Multimeter_1 = None  # Multimeter_1()
        """:type : Multimeter_1"""
        self.FunctionGenerator = None  # FunctionGenerator()
        """:type : FrequencyGenerator"""
        self.DCPowerSupply = None  # DCPowerSupply_1()
        """:type : DCPowerSupply_1"""
        self.DCPowerSupply2 = None  # DCPowerSupply_2()
        """:type : DCPowerSupply_2"""
        self.TemperatureChamber = None  # DCPowerSupply_2()
        """:type : DCPowerSupply_2"""


