from __future__ import annotations

from abc import abstractmethod

from .ivi_driver_wrapper import IVIDriverWrapper


class IVICDriverWrapper(IVIDriverWrapper):
    def __init__(self, *args, **kwargs):
        pass

    def __str__(self):
        return f"{self.drivername}(IVI-C driver wrapper)"

    @property
    @abstractmethod
    def const(self):
        pass

    @property
    @abstractmethod
    def dll(self):
        pass
