from __future__ import annotations


class IIviACPwrDCGeneration:
    def __init__(self):
        pass

    @property
    def Mode(self):
        raise NotImplementedError

    @Mode.setter
    def Mode(self, value):
        """
        Output voltage generation mode. This can be AC Only, DC Only or AC Plus DC.
        """
        raise NotImplementedError

    @property
    def DCVoltageLevel(self):
        raise NotImplementedError

    @DCVoltageLevel.setter
    def DCVoltageLevel(self, value):
        """
        The DC output voltage when the DC Generation Mode property is set to DC Only or
        the DC offset level when the DC Generation Mode property is set to AC Plus DC.
        """
        raise NotImplementedError

    @property
    def RangeMin(self):
        raise NotImplementedError

    @property
    def NumRanges(self):
        raise NotImplementedError

    @property
    def RangeMax(self):
        raise NotImplementedError

    def Configure(self, Mode, DCVoltageLevel):
        """
        Configures the DC Generation Mode and DCVoltageLevel properties.

        :param Mode: Sets the DC Generation Mode property.
        :type Mode: IviACPwrDCGenerationEnum
        :param DCVoltageLevel: Sets the DC Generation DC Voltage Level property.
        :type DCVoltageLevel: float
        """
        raise NotImplementedError

    def QueryCapabilities(self, Range):
        """
        Returns the minimum and maximum DC voltage supported by the specified range.

        :param Range: The range to query. The Range parameter must be from 1 to the value of the
            Number of Ranges property.
        :type Range: int
        """
        raise NotImplementedError

    def ConfigureRange(self, Minimum, Maximum):
        """
        Configures the DC voltage range of the power source. The driver sets the power
        source to the lowest range that supports the minimum and maximum voltages
        specified by the Minimum and Maximum parameters. It then updates the DC Range
        Min and DC Range Max properties.

        :param Minimum: Requests a range that supports this minimum DC voltage.
        :type Minimum: float
        :param Maximum: Requests a range that supports this maximum DC voltage.
        :type Maximum: float
        """
        raise NotImplementedError
