from __future__ import annotations


class IIviCounterEdgeTime:
    def __init__(self):
        pass

    @property
    def Channel(self):
        raise NotImplementedError

    @Channel.setter
    def Channel(self, value):
        """
        Specifies the input channel the edge time is measured on.
        """
        raise NotImplementedError

    @property
    def Estimate(self):
        raise NotImplementedError

    @Estimate.setter
    def Estimate(self, value):
        """
        Specifies the estimated edge time, in seconds, for the edge time function.
        """
        raise NotImplementedError

    @property
    def Resolution(self):
        raise NotImplementedError

    @Resolution.setter
    def Resolution(self, value):
        """
        Specifies the resolution of the measurement, in seconds, for the edge time
        function.
        """
        raise NotImplementedError

    @property
    def ReferenceType(self):
        raise NotImplementedError

    @ReferenceType.setter
    def ReferenceType(self, value):
        """
        Specifies the current reference type of the Counter.
        """
        raise NotImplementedError

    @property
    def HighReference(self):
        raise NotImplementedError

    @HighReference.setter
    def HighReference(self, value):
        """
        Specifies the high reference voltage level in volts for the edge time function.
        """
        raise NotImplementedError

    @property
    def LowReference(self):
        raise NotImplementedError

    @LowReference.setter
    def LowReference(self, value):
        """
        Specifies the low reference voltage level in volts for the edge time function.
        """
        raise NotImplementedError

    def Configure(self, Channel, Estimate, Resolution):
        """
        Configures an edge time measurement.

        :param Channel: Specifies the channel the edge time will be measured on.
        :type Channel: str
        :param Estimate: Specifies the estimated edge time.
        :type Estimate: float
        :param Resolution: Specifies the resolution of the edge time measurement. It is the quantization
            size, i.e. the smallest delta value that can be detected.
        :type Resolution: float
        """
        raise NotImplementedError

    def ConfigureReferenceLevels(self, Channel, ReferenceType, Estimate, Resolution, HighReference, LowReference):
        """
        Configures the reference type, estimate, resolution, high reference level, and
        low reference level attributes for an edge time measurement.

        :param Channel: Specifies the channel the edge time will be measured on.
        :type Channel: str
        :param ReferenceType: Specifies the reference type.
        :type ReferenceType: IviCounterReferenceTypeEnum
        :param Estimate: Specifies the estimated edge time.
        :type Estimate: float
        :param Resolution: Specifies the resolution of the edge time measurement. It is the quantization
            size, i.e. the smallest delta value that can be detected.
        :type Resolution: float
        :param HighReference: Specifies the high reference level for the edge time.
        :type HighReference: float
        :param LowReference: Specifies the low reference level for the edge time.
        :type LowReference: float
        """
        raise NotImplementedError
