from __future__ import annotations

import os
from os.path import *


def getProjectPath():
    """Return the path to the project source."""
    return os.path.abspath(os.path.join(os.path.dirname(__file__), "../../.."))


def join_and_make_absolute(path, *paths):
    """
    Joins paths and normalize (abspath + normpath)

    :param path: base path
    :type path: str
    :param paths: list of paths
    :type paths: str
    :return:
    """
    p = join(path, *paths)
    return abspath(p)


if __name__ == "__main__":
    print(getProjectPath())
