from __future__ import annotations

from monocle_telemetry.api.feature import track_feature as _track_feature

from pverify.version import __version_tuple__


def track_feature(feature: str, misc: str = ""):
    _track_feature(
        project="pyveco",
        tool="ifx-pyverify",
        version=".".join(map(str, __version_tuple__[:3])),
        feature=f"pyverify_{feature}",
        misc=misc,
    )


class FEATURES:
    pyverify_import = "import"
    pyverify_mode_lab = "mode_lab"
    pyverify_mode_sim = "mode_sim"
    pyverify_mode_pp = "mode_pp"
    pyverify_run_bare = "run_bare"
    pyverify_run_xhub = "run_xhub"
    pyverify_reviz = "reviz"
