from __future__ import annotations

from . import Bode100Enums


class Bode100Receiver:
    def __init__(self, BodeDoc, MeasurementObj, Index):
        self.Enums = Bode100Enums
        self._BodeDoc = BodeDoc
        self.__index = Index
        self._MeasurementObj = MeasurementObj

    @property
    def Attenuator(self):
        return int(self._MeasurementObj.DeviceSettings.Receivers[self.__index].Attenuator)

    @Attenuator.setter
    def Attenuator(self, value):
        """
        Gets or sets the receiver attenuator value in dB (0, 10, 20, 30 or 40 dB)

        :type value: Bode100Enums.Attenuator
        """
        self._MeasurementObj.DeviceSettings.Receivers[self.__index].Attenuator = int(value)

    @property
    def Input(self):
        return int(self._MeasurementObj.DeviceSettings.Receivers[self.__index].Input)

    @Input.setter
    def Input(self, value):
        """
        Gets or sets the routing of the receiver input. (Channel:0, Source:1)

        :type value: Bode100Enums.ReceiverInput
        """
        self._MeasurementObj.DeviceSettings.Receivers[self.__index].Input = int(value)
