from __future__ import annotations

from pverify.drivers.CAN_Controller.PCAN.PCANBasic import *


class Enums:
    PCAN_ERRORS = {
        TPCANStatus(0x00001): "PCAN_ERROR_XMTFULL - Transmit buffer in CAN controller is full",
        TPCANStatus(0x00002): "PCAN_ERROR_OVERRUN - CAN controller was read too late",
        TPCANStatus(0x00004): "PCAN_ERROR_BUSLIGHT - Bus error: an error counter reached the 'light' limit",
        TPCANStatus(0x00008): "PCAN_ERROR_BUSHEAVY - Bus error: an error counter reached the 'heavy' limit",
        TPCANStatus(
            PCAN_ERROR_BUSHEAVY
        ): "PCAN_ERROR_BUSWARNING - Bus error: an error counter reached the 'warning' limit",
        TPCANStatus(0x40000): "PCAN_ERROR_BUSPASSIVE - Bus error: the CAN controller is error passive",
        TPCANStatus(0x00010): "PCAN_ERROR_BUSOFF - Bus error: the CAN controller is in bus-off state",
        TPCANStatus(
            PCAN_ERROR_BUSWARNING
            | PCAN_ERROR_BUSLIGHT
            | PCAN_ERROR_BUSHEAVY
            | PCAN_ERROR_BUSOFF
            | PCAN_ERROR_BUSPASSIVE
        ): "PCAN_ERROR_ANYBUSERR - Mask for all bus errors",
        TPCANStatus(0x00020): "PCAN_ERROR_QRCVEMPTY - Receive queue is empty",
        TPCANStatus(0x00040): "PCAN_ERROR_QOVERRUN - Receive queue was read too late",
        TPCANStatus(0x00080): "PCAN_ERROR_QXMTFULL - Transmit queue is full",
        TPCANStatus(
            0x00100
        ): "PCAN_ERROR_REGTEST - Test of the CAN controller hardware registers failed (no hardware found)",
        TPCANStatus(0x00200): "PCAN_ERROR_NODRIVER - Driver not loaded",
        TPCANStatus(0x00400): "PCAN_ERROR_HWINUSE - Hardware already in use by a Net",
        TPCANStatus(0x00800): "PCAN_ERROR_NETINUSE - A Client is already connected to the Net",
        TPCANStatus(0x01400): "PCAN_ERROR_ILLHW - Hardware handle is invalid",
        TPCANStatus(0x01800): "PCAN_ERROR_ILLNET - Net handle is invalid",
        TPCANStatus(0x01C00): "PCAN_ERROR_ILLCLIENT - Client handle is invalid",
        TPCANStatus(
            PCAN_ERROR_ILLHW | PCAN_ERROR_ILLNET | PCAN_ERROR_ILLCLIENT
        ): "PCAN_ERROR_ILLHANDLE - Mask for all handle errors",
        TPCANStatus(0x02000): "PCAN_ERROR_RESOURCE - Resource (FIFO, Client, timeout) cannot be created",
        TPCANStatus(0x04000): "PCAN_ERROR_ILLPARAMTYPE - Invalid parameter",
        TPCANStatus(0x08000): "PCAN_ERROR_ILLPARAMVAL - Invalid parameter value",
        TPCANStatus(0x10000): "PCAN_ERROR_UNKNOWN - Unknown error",
        TPCANStatus(0x20000): "PCAN_ERROR_ILLDATA - Invalid data, function, or action",
        TPCANStatus(
            0x2000000
        ): "PCAN_ERROR_CAUTION - An operation was successfully carried out, however, irregularities were registered",
        TPCANStatus(
            0x4000000
        ): "PCAN_ERROR_INITIALIZE - Channel is not initialized [Value was changed from 0x40000 to 0x4000000]",
        TPCANStatus(
            0x8000000
        ): "PCAN_ERROR_ILLOPERATION - Invalid operation [Value was changed from 0x80000 to 0x8000000]",
    }

    class PCAN_BAUDRATES:
        BD_1M = PCAN_BAUD_1M
        BD_800K = PCAN_BAUD_800K
        BD_500K = PCAN_BAUD_500K
        BD_250K = PCAN_BAUD_250K
        BD_125K = PCAN_BAUD_125K
        BD_100K = PCAN_BAUD_100K
        BD_95K = PCAN_BAUD_95K
        BD_83K = PCAN_BAUD_83K
        BD_50K = PCAN_BAUD_50K
        BD_47K = PCAN_BAUD_47K
        BD_33K = PCAN_BAUD_33K
        BD_20K = PCAN_BAUD_20K
        BD_10K = PCAN_BAUD_10K
        BD_5K = PCAN_BAUD_5K

    class PCAN_MESSAGE_TYPE:
        STANDARD = TPCANMessageType(0x00)  # The PCAN message is a CAN Standard Frame (11-bit identifier)
        RTR = TPCANMessageType(0x01)  # The PCAN message is a CAN Remote-Transfer-Request Frame
        EXTENDED = TPCANMessageType(0x02)  # The PCAN message is a CAN Extended Frame (29-bit identifier)
        FD = TPCANMessageType(0x04)  # The PCAN message represents a FD frame in terms of CiA Specs
        BRS = TPCANMessageType(0x08)  # The PCAN message represents a FD bit rate switch (CAN data at a higher bit rate)
        ESI = TPCANMessageType(
            0x10
        )  # The PCAN message represents a FD error state indicator(CAN FD transmitter was error active)
        STATUS = TPCANMessageType(0x80)  # The PCAN message represents a PCAN status message

    class PCAN_FRAME_TYPE:
        STANDARD = PCAN_MESSAGE_STANDARD
        EXTENDED = PCAN_MESSAGE_EXTENDED

    class PCAN_STATUS_CODES:
        PCAN_ERROR_OK = TPCANStatus(0x00000)  # No error
        PCAN_ERROR_XMTFULL = TPCANStatus(0x00001)  # Transmit buffer in CAN controller is full
        PCAN_ERROR_OVERRUN = TPCANStatus(0x00002)  # CAN controller was read too late
        PCAN_ERROR_BUSLIGHT = TPCANStatus(0x00004)  # Bus error: an error counter reached the 'light' limit
        PCAN_ERROR_BUSHEAVY = TPCANStatus(0x00008)  # Bus error: an error counter reached the 'heavy' limit
        PCAN_ERROR_BUSWARNING = TPCANStatus(
            PCAN_ERROR_BUSHEAVY
        )  # Bus error: an error counter reached the 'warning' limit
        PCAN_ERROR_BUSPASSIVE = TPCANStatus(0x40000)  # Bus error: the CAN controller is error passive
        PCAN_ERROR_BUSOFF = TPCANStatus(0x00010)  # Bus error: the CAN controller is in bus-off state
        PCAN_ERROR_ANYBUSERR = TPCANStatus(
            PCAN_ERROR_BUSWARNING
            | PCAN_ERROR_BUSLIGHT
            | PCAN_ERROR_BUSHEAVY
            | PCAN_ERROR_BUSOFF
            | PCAN_ERROR_BUSPASSIVE
        )  # Mask for all bus errors
        PCAN_ERROR_QRCVEMPTY = TPCANStatus(0x00020)  # Receive queue is empty
        PCAN_ERROR_QOVERRUN = TPCANStatus(0x00040)  # Receive queue was read too late
        PCAN_ERROR_QXMTFULL = TPCANStatus(0x00080)  # Transmit queue is full
        PCAN_ERROR_REGTEST = TPCANStatus(
            0x00100
        )  # Test of the CAN controller hardware registers failed (no hardware found)
        PCAN_ERROR_NODRIVER = TPCANStatus(0x00200)  # Driver not loaded
        PCAN_ERROR_HWINUSE = TPCANStatus(0x00400)  # Hardware already in use by a Net
        PCAN_ERROR_NETINUSE = TPCANStatus(0x00800)  # A Client is already connected to the Net
        PCAN_ERROR_ILLHW = TPCANStatus(0x01400)  # Hardware handle is invalid
        PCAN_ERROR_ILLNET = TPCANStatus(0x01800)  # Net handle is invalid
        PCAN_ERROR_ILLCLIENT = TPCANStatus(0x01C00)  # Client handle is invalid
        PCAN_ERROR_ILLHANDLE = TPCANStatus(
            PCAN_ERROR_ILLHW | PCAN_ERROR_ILLNET | PCAN_ERROR_ILLCLIENT
        )  # Mask for all handle errors
        PCAN_ERROR_RESOURCE = TPCANStatus(0x02000)  # Resource (FIFO, Client, timeout) cannot be created
        PCAN_ERROR_ILLPARAMTYPE = TPCANStatus(0x04000)  # Invalid parameter
        PCAN_ERROR_ILLPARAMVAL = TPCANStatus(0x08000)  # Invalid parameter value
        PCAN_ERROR_UNKNOWN = TPCANStatus(0x10000)  # Unknown error
        PCAN_ERROR_ILLDATA = TPCANStatus(0x20000)  # Invalid data, function, or action
        PCAN_ERROR_CAUTION = TPCANStatus(
            0x2000000
        )  # An operation was successfully carried out, however, irregularities were registered
        PCAN_ERROR_INITIALIZE = TPCANStatus(
            0x4000000
        )  # Channel is not initialized [Value was changed from 0x40000 to 0x4000000]
        PCAN_ERROR_ILLOPERATION = TPCANStatus(
            0x8000000
        )  # Invalid operation [Value was changed from 0x80000 to 0x8000000]
