from __future__ import annotations

from .ag5313xni import *


class IIviCounterArmStart:
    def __init__(self, root):
        self.root = root

        from .IIviCounterArmStartExternal import IIviCounterArmStartExternal

        self.External = IIviCounterArmStartExternal(self.root)

    @property
    def Type(self):
        return self.root.dllwrap.GetAttributeViInt32(None, IVI_CLASS_PUBLIC_ATTR_BASE + 53)

    @Type.setter
    def Type(self, value):
        """
        Specifies the start arm type for armed measurements.
        """
        self.root.dllwrap.SetAttributeViInt32(None, IVI_CLASS_PUBLIC_ATTR_BASE + 53, value)
