from __future__ import annotations

from .ag5313xni import *


class IIviCounterArmStop:
    def __init__(self, root):
        self.root = root

        from .IIviCounterArmStopExternal import IIviCounterArmStopExternal

        self.External = IIviCounterArmStopExternal(self.root)

    @property
    def Type(self):
        return self.root.dllwrap.GetAttributeViInt32(None, IVI_CLASS_PUBLIC_ATTR_BASE + 58)

    @Type.setter
    def Type(self, value):
        """
        Specifies the stop arm type for armed measurements.
        """
        self.root.dllwrap.SetAttributeViInt32(None, IVI_CLASS_PUBLIC_ATTR_BASE + 58, value)
