from __future__ import annotations

from .ag5313xni import *


class IIviCounterPeriod:
    def __init__(self, root):
        self.root = root

    @property
    def Channel(self):
        return self.root.dllwrap.GetAttributeViString(None, IVI_CLASS_PUBLIC_ATTR_BASE + 18, 1024)

    @Channel.setter
    def Channel(self, value):
        """
        Specifies the input channel the period is measured on.
        """
        self.root.dllwrap.SetAttributeViString(None, IVI_CLASS_PUBLIC_ATTR_BASE + 18, value)

    @property
    def Estimate(self):
        return self.root.dllwrap.GetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 19)

    @Estimate.setter
    def Estimate(self, value):
        """
        Specifies the estimated period, in seconds, for the period function.
        """
        self.root.dllwrap.SetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 19, value)

    @property
    def Resolution(self):
        return self.root.dllwrap.GetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 20)

    @Resolution.setter
    def Resolution(self, value):
        """
        Specifies the resolution of the measurement, in seconds, for the period
        function.
        """
        self.root.dllwrap.SetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 20, value)

    @property
    def ApertureTime(self):
        return self.root.dllwrap.GetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 21)

    @ApertureTime.setter
    def ApertureTime(self, value):
        """
        Specifies the aperture time in seconds for the period with aperture time
        function.
        """
        self.root.dllwrap.SetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 21, value)

    def ConfigureWithAperture(self, Channel, ApertureTime):
        """
        Configures a period measurement based on the specified aperture time.

        :param Channel: Specifies the channel the period will be measured on.
        :type Channel: str
        :param ApertureTime: Specifies the aperture time of the period measurement.
        :type ApertureTime: float
        """
        self.root.dllwrap.ConfigurePeriodWithApertureTime(Channel, ApertureTime)

    def Configure(self, Channel, Estimate, Resolution):
        """
        Configures the estimate and resolution attributes for a period measurement.

        :param Channel: Specifies the channel the period will be measured on.
        :type Channel: str
        :param Estimate: Specifies the estimated period.
        :type Estimate: float
        :param Resolution: Specifies the resolution of the period measurement. It is the quantization size,
            i.e. the smallest delta value that can be detected.
        :type Resolution: float
        """
        self.root.dllwrap.ConfigurePeriod(Channel, Estimate, Resolution)
