from __future__ import annotations

from .ag5313xni import *


class IIviCounterTimeInterval:
    def __init__(self, root):
        self.root = root

    @property
    def StartChannel(self):
        return self.root.dllwrap.GetAttributeViString(None, IVI_CLASS_PUBLIC_ATTR_BASE + 39, 1024)

    @StartChannel.setter
    def StartChannel(self, value):
        """
        Specifies the start channel used to perform the measure time interval function.
        """
        self.root.dllwrap.SetAttributeViString(None, IVI_CLASS_PUBLIC_ATTR_BASE + 39, value)

    @property
    def StopChannel(self):
        return self.root.dllwrap.GetAttributeViString(None, IVI_CLASS_PUBLIC_ATTR_BASE + 40, 1024)

    @StopChannel.setter
    def StopChannel(self, value):
        """
        Specifies the stop channel used to perform the measure time interval function.
        """
        self.root.dllwrap.SetAttributeViString(None, IVI_CLASS_PUBLIC_ATTR_BASE + 40, value)

    @property
    def Estimate(self):
        return self.root.dllwrap.GetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 41)

    @Estimate.setter
    def Estimate(self, value):
        """
        Specifies the estimated time interval, in seconds, for the time interval
        function.
        """
        self.root.dllwrap.SetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 41, value)

    @property
    def Resolution(self):
        return self.root.dllwrap.GetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 42)

    @Resolution.setter
    def Resolution(self, value):
        """
        Specifies the resolution of the measurement, in seconds, for the time interval
        function.
        """
        self.root.dllwrap.SetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 42, value)

    @property
    def StopHoldoff(self):
        return self.root.dllwrap.GetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 601)

    @StopHoldoff.setter
    def StopHoldoff(self, value):
        """
        Specifies the stop holdoff time in seconds for a Time Interval measurement.
        """
        self.root.dllwrap.SetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 601, value)

    def Configure(self, StartChannel, StopChannel, Estimate, Resolution):
        """
        Configures the estimate and resolution attributes and specifies the start and
        stop channels for a time interval measurement.

        :param StartChannel: Specifies the channel that will start the time interval measurement.
        :type StartChannel: str
        :param StopChannel: Specifies the channel that will stop the time interval measurement.
        :type StopChannel: str
        :param Estimate: Specifies the estimated time interval.
        :type Estimate: float
        :param Resolution: Specifies the resolution of the time interval measurement. It is the
            quantization size, i.e. the smallest delta value that can be detected.
        :type Resolution: float
        """
        self.root.dllwrap.ConfigureTimeInterval(StartChannel, StopChannel, Estimate, Resolution)
