from __future__ import annotations

from .AgN57xx import *


class IIviDCPwrTrigger:
    def __init__(self, root):
        self.root = root
        """:type : pverify.drivers.DCPwr.AgN57xx.IIviDCPwr.IIviDCPwr"""

    def Abort(self):
        """
        method Abort returns the power supply to the ignore triggers state if the power
        supply is currently waiting for a trigger to change the output signal. If the
        power supply is not waiting for a trigger, this method does nothing.
        """
        self.root.dllwrap.Abort()

    def Initiate(self):
        """
        method Initiate causes the power supply to wait for a trigger if the power
        supply is not currently waiting for a trigger. If the power supply is already
        waiting for a trigger, this method does nothing
        """
        self.root.dllwrap.Initiate()

    def SendSoftwareTrigger(self):
        """
        method Send Software Trigger supplies a trigger signal when the trigger source
        is set to Software Trigger
        """
        self.root.dllwrap.SendSoftwareTrigger()
