from __future__ import annotations

import os

USING_WINDOWS = os.name == "nt"


def print_red(x):
    if USING_WINDOWS:
        print(x)
    else:
        print(f"\033[91m {x}\033[00m")


def print_green(x):
    if USING_WINDOWS:
        print(x)
    else:
        print(f"\033[92m {x}\033[00m")


def print_cyan(x):
    if USING_WINDOWS:
        print(x)
    else:
        print(f"\033[96m {x}\033[00m")


def fmt_green(x):
    if USING_WINDOWS:
        return x
    else:
        return f"\033[92m {x}\033[00m"


def fmt_cyan(x):
    if USING_WINDOWS:
        return x
    else:
        return f"\033[96m {x}\033[00m"
