from __future__ import annotations

from .Ag34401 import *


class IIviDmmThermocouple:
    def __init__(self, root):
        self.root = root

    @property
    def FixedRefJunction(self):
        return self.root.dllwrap.GetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 233)

    @FixedRefJunction.setter
    def FixedRefJunction(self, value):
        """
        The external reference junction temperature when a fixed reference junction
        thermocouple is used to measure temperature, or the thermocouple junction
        temperature of an instrument without an internal temperature sensor, in degrees
        Celcius.
        """
        self.root.dllwrap.SetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 233, value)

    @property
    def RefJunctionType(self):
        return self.root.dllwrap.GetAttributeViInt32(None, IVI_CLASS_PUBLIC_ATTR_BASE + 232)

    @RefJunctionType.setter
    def RefJunctionType(self, value):
        """
        The type of reference junction to be used in the reference junction compensation
        of a thermocouple measurement. Applies only when the Temperature Transducer Type
        is Thermocouple.
        """
        self.root.dllwrap.SetAttributeViInt32(None, IVI_CLASS_PUBLIC_ATTR_BASE + 232, value)

    @property
    def Type(self):
        return self.root.dllwrap.GetAttributeViInt32(None, IVI_CLASS_PUBLIC_ATTR_BASE + 231)

    @Type.setter
    def Type(self, value):
        """
        The type of thermocouple used to measure the temperature. Applies only when the
        Temperature Transducer Type is Thermocouple.
        """
        self.root.dllwrap.SetAttributeViInt32(None, IVI_CLASS_PUBLIC_ATTR_BASE + 231, value)

    def Configure(self, Type, RefJunctionType):
        """
        Configures the thermocouple Type and RefJunctionType properties of a
        thermocouple. Applies only when the Temperature Transducer Type is Thermocouple.

        :param Type: The type of thermocouple used to measure the temperature.
        :type Type: IviDmmThermocoupleTypeEnum
        :param RefJunctionType: The type of reference junction used in the reference junction compensation.
        :type RefJunctionType: IviDmmRefJunctionTypeEnum
        """
        self.Type = Type
        self.RefJunctionType = RefJunctionType
