from __future__ import annotations

from .Ag34410 import *


class IIviDmmRTD:
    def __init__(self, root):
        self.root = root
        """:type : pverify.drivers.Dmm.Ag34410.IIviDmm.IIviDmm"""

    @property
    def Alpha(self):
        return self.root.dllwrap.GetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 241)

    @Alpha.setter
    def Alpha(self, value):
        """
        The alpha parameter for a resistance temperature device (RTD). Applies only when
        the Temperature Transducer Type is set to 2 Wire RTD or 4 Wire RTD.
        """
        self.root.dllwrap.SetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 241, value)

    @property
    def Resistance(self):
        return self.root.dllwrap.GetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 242)

    @Resistance.setter
    def Resistance(self, value):
        """
        The R0 parameter (resistance) for a resistance temperature device (RTD). Also
        known as the RTD reference value. Applies only when the Temperature Transducer
        Type is set to 2 Wire RTD or 4 Wire RTD.
        """
        self.root.dllwrap.SetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 242, value)

    def Configure(self, Alpha, Resistance):
        """
        Configures the Alpha and Resistance parameters for a resistance temperature
        device.

        :param Alpha: The alpha parameter for a resistance temperature device (RTD).
        :type Alpha: float
        :param Resistance: The R0 parameter (resistance) for a resistance temperature device (RTD) also
            known as the RTD reference value.
        :type Resistance: float
        """
        self.Alpha = Alpha
        self.Resistance = Resistance
