from __future__ import annotations

from .ag3446x import *


class IIviDmmTrigger:
    def __init__(self, root):
        self.root = root
        """:type : pverify.drivers.Dmm.ag3446x.IIviDmm.IIviDmm"""

        from .IIviDmmMultiPoint import IIviDmmMultiPoint

        self.MultiPoint = IIviDmmMultiPoint(self.root)

    @property
    def Slope(self):
        return self.root.dllwrap.GetAttributeViInt32(None, IVI_CLASS_PUBLIC_ATTR_BASE + 334)

    @Slope.setter
    def Slope(self, value):
        """
        The polarity of the external trigger slope, which determines whether the DMM
        triggers on either the rising or the falling edge of the external trigger
        source.
        """
        self.root.dllwrap.SetAttributeViInt32(None, IVI_CLASS_PUBLIC_ATTR_BASE + 334, value)

    @property
    def Delay(self):
        return self.root.dllwrap.GetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 5)

    @Delay.setter
    def Delay(self, value):
        """
        The interval between the time when the DMM receives the trigger and the time
        when it takes a measurement. Positive values set the trigger delay in seconds.
        Negative values set auto delay mode.
        """
        self.root.dllwrap.SetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 5, value)

    @property
    def Source(self):
        return self.root.dllwrap.GetAttributeViInt32(None, IVI_CLASS_PUBLIC_ATTR_BASE + 4)

    @Source.setter
    def Source(self, value):
        """
        The trigger source.
        """
        self.root.dllwrap.SetAttributeViInt32(None, IVI_CLASS_PUBLIC_ATTR_BASE + 4, value)

    def Configure(self, TriggerSource, TriggerDelay):
        """
        Configures the trigger Source and Delay properties.

        :param TriggerSource: Trigger source.
        :type TriggerSource: IviDmmTriggerSourceEnum
        :param TriggerDelay: The length of time between when the DMM receives the trigger and when it takes a
            measurement. Use positive values to set the trigger delay in seconds. Negative
            values are reserved for the auto delay mode.
        :type TriggerDelay: float
        """
        self.Source = TriggerSource
        self.Delay = TriggerDelay
