from __future__ import annotations

from .fl884x import *


class IIviDmmThermistor:
    def __init__(self, root):
        self.root = root

    @property
    def Resistance(self):
        return self.root.dllwrap.GetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 251)

    @Resistance.setter
    def Resistance(self, value):
        """
        The resistance of the thermistor in Ohms. Applies only when the Temperature
        Transducer Type property is Thermistor.
        """
        self.root.dllwrap.SetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 251, value)
