from __future__ import annotations

from .ke2000 import *


class IIviDmmFrequency:
    def __init__(self, root):
        self.root = root

    @property
    def VoltageRange(self):
        return self.root.dllwrap.GetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 101)

    @VoltageRange.setter
    def VoltageRange(self, value):
        """
        The expected maximum voltage level of the input signal for frequency and period
        measurements. Positive values set the manual range. Negative values set Auto
        Range mode. The units are specified in Volts RMS.
        """
        self.root.dllwrap.SetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 101, value)
