from __future__ import annotations

from .Ag3352x import *


class IIviFgenAM:
    def __init__(self, root):
        self.root = root

    @property
    def InternalDepth(self):
        return self.root.dllwrap.GetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 403)

    @InternalDepth.setter
    def InternalDepth(self, value):
        """
        The extent of modulation the function generator applies to the carrier waveform
        when the AM Source attribute is set to AM Internal. The unit is percentage.
        Applies only when the AM Source attribute is set to AM Internal.
        """
        self.root.dllwrap.SetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 403, value)

    @property
    def InternalFrequency(self):
        return self.root.dllwrap.GetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 405)

    @InternalFrequency.setter
    def InternalFrequency(self, value):
        """
        The frequency of the internal modulating waveform source. The units are Hertz.
        Applies only when the AM Source attribute is set to AM Internal.
        """
        self.root.dllwrap.SetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 405, value)

    @property
    def InternalWaveform(self):
        return self.root.dllwrap.GetAttributeViInt32(None, IVI_CLASS_PUBLIC_ATTR_BASE + 404)

    @InternalWaveform.setter
    def InternalWaveform(self, value):
        """
        The waveform of the internal modulating waveform source. Applies only when the
        AM Source attribute is set to AM Internal.
        """
        self.root.dllwrap.SetAttributeViInt32(None, IVI_CLASS_PUBLIC_ATTR_BASE + 404, value)

    def ConfigureInternal(self, Depth, Waveform, Frequency):
        """
        Configures the attributes that control the function generator's internal
        amplitude modulating waveform source.

        :param Depth: Specifies the internal modulation depth. This value sets the AM Internal Depth
            property.
        :type Depth: float
        :param Waveform: Specifies the waveform the function generator uses for the internal modulating
            waveform source. This value sets the AM Internal Waveform property.
        :type Waveform: IviFgenAMInternalWaveformEnum
        :param Frequency: Specifies the frequency of the internal modulating waveform source. This value
            sets the AM Internal Frequency property.
        :type Frequency: float
        """
        self.root.dllwrap.ConfigureAMInternal(Depth, Waveform, Frequency)

    def get_Enabled(self, ChannelName):
        """
        Provides an indication whether AM is enabled on a channel.

        :param ChannelName: The name of a channel.
        :type ChannelName: str

        :return: True if the function generator applies amplitude modulation to the signal that
            the function generator produces.
        :rtype: bool
        """
        return self.root.dllwrap.GetAttributeViBoolean(ChannelName, IVI_CLASS_PUBLIC_ATTR_BASE + 401)

    def get_Source(self, ChannelName):
        """
        The source of the signal that the function generator uses as the modulating
        waveform. Applies only when the AM Enabled attribute is set to True.

        :param ChannelName: The name of a channel.
        :type ChannelName: str

        :return: One of the values of IviFgenAMSourceEnum.
        :rtype: int
        """
        return self.root.dllwrap.GetAttributeViInt32(ChannelName, IVI_CLASS_PUBLIC_ATTR_BASE + 402)

    def set_Enabled(self, ChannelName, pVal):
        """
        Enables or disables AM on a channel.

        :param ChannelName: The name of a channel.
        :type ChannelName: str
        :param pVal: True enables AM.
        :type pVal: bool
        """
        self.root.dllwrap.SetAttributeViBoolean(ChannelName, IVI_CLASS_PUBLIC_ATTR_BASE + 401, pVal)

    def set_Source(self, ChannelName, pVal):
        """
        Selects the source of the signal that the function generator uses as the
        modulating waveform. Applies only when the AM Enabled attribute is set to True.

        :param ChannelName: The name of a channel.
        :type ChannelName: str
        :param pVal: One of the values of IviFgenAMSourceEnum.
        :type pVal: IviFgenAMSourceEnum
        """
        self.root.dllwrap.SetAttributeViInt32(ChannelName, IVI_CLASS_PUBLIC_ATTR_BASE + 402, pVal)
