from __future__ import annotations

from .IIviFgenBase import IIviFgenBase


class IIviDriverIdentity(IIviFgenBase):
    def __init__(self, parent=None):
        super().__init__(self)
        self.parent = parent

    @property
    def Description(self):
        return "Custom PyVerify IVI-SCPI driver for RedPitaya"

    @property
    def Revision(self):
        return "unknown"

    @property
    def Vendor(self):
        raise NotImplementedError

    @property
    def InstrumentManufacturer(self):
        return "RedPitaya"

    @property
    def InstrumentModel(self):
        return "RedPitaya"

    @property
    def InstrumentFirmwareRevision(self):
        raise NotImplementedError

    @property
    def Identifier(self):
        return self.vi_query("*IDN?")

    @property
    def SupportedInstrumentModels(self):
        return "RedPitaya"

    @property
    def SpecificationMajorVersion(self):
        raise NotImplementedError

    @property
    def SpecificationMinorVersion(self):
        raise NotImplementedError

    @property
    def GroupCapabilities(self):
        return "unknown"
