from __future__ import annotations


class IviFgenOutputModeEnum:
    IviFgenOutputModeFunction = 0  # The driver uses the attributes and functions of the IviFgenStdFunc extension group to configure the function generator's output signal.
    IviFgenOutputModeArbitrary = 1  # The driver uses the attributes and functions of the IviFgenArbWfm extension group to configure the function generator's output signal.
    IviFgenOutputModeSequence = 2  # The driver uses the attributes and functions of the IviFgenArbSeq extension group to configure the function generator's output signal.


class IviFgenOperationModeEnum:
    IviFgenOperationModeContinuous = (
        0  # When in the Output Generation State, the function generator produces output continuously.
    )
    IviFgenOperationModeBurst = 1  # When in the Output Generation State, the function generator produces a burst of waveform cycles based on a trigger condition. A burst consists of a discrete number of waveform cycles.  The user uses the attribute of the IviFgenTrigger Extension Group to configure the trigger, and the attributes of the IviFgenBurst extension group to configure how the function generator produces bursts.


class IviFgenReferenceClockSourceEnum:
    IviFgenReferenceClockSourceInternal = 0  # The function generator produces the reference clock signal internally.
    IviFgenReferenceClockSourceExternal = (
        1  # The function generator receives the reference clock signal from an external source.
    )
    IviFgenReferenceClockSourceRTSI = (
        101  # The function generator receives the reference clock signal from the RTSI clock source.
    )


