class IviFgenWaveformEnum:
    IviFgenWaveformSine = 1  # Configures the function generator to produce a sinusoid waveform.
    IviFgenWaveformSquare = 2  # Configures the function generator to produce a square waveform.
    IviFgenWaveformTriangle = 3  # Configures the function generator to produce a triangular waveform.
    IviFgenWaveformRampUp = 4  # Configures the function generator to produce a positive ramp waveform.
    IviFgenWaveformRampDown = 5  # Configures the function generator to produce a negative ramp waveform.
    IviFgenWaveformDC = 6  # Configures the function generator to produce a constant voltage.


class IviFgenAMInternalWaveformEnum:
    IviFgenAMInternalWaveformSine = 1  # The function generator uses a sinusoid waveform as the modulating waveform.
    IviFgenAMInternalWaveformSquare = 2  # The function generator uses a square waveform as the modulating waveform.
    IviFgenAMInternalWaveformTriangle = 3  # The function generator uses a triangle waveform as the modulating waveform.
    IviFgenAMInternalWaveformRampUp = (
        4  # The function generator uses a positive ramp waveform as the modulating waveform.
    )
    IviFgenAMInternalWaveformRampDown = (
        5  # The function generator uses a negative ramp waveform as the modulating waveform.
    )


class IviFgenAMSourceEnum:
    IviFgenAMSourceInternal = 0  # The function generator uses an internally generated waveform as the modulating waveform.  You use the AM Internal Depth, AM Internal Waveform, and AM Internal Frequency attributes to configure the internally generated modulating waveform.
    IviFgenAMSourceExternal = (
        1  # The function generator uses a waveform from an external source as the modulating waveform.
    )


