from __future__ import annotations


class IIviDriverIdentity:
    def __init__(self, parent=None):
        self.parent = parent
        if False:
            from .IIfxPwrAn import IIfxPwrAn

            self.parent = IIfxPwrAn()

    @property
    def Description(self):
        return "Custom PyVerify IVI-SCPI driver for Yokogawa WT500 Series"

    @property
    def Revision(self):
        raise NotImplementedError

    @property
    def Vendor(self):
        raise NotImplementedError

    @property
    def InstrumentManufacturer(self):
        return "Yokogawa"

    @property
    def InstrumentModel(self):
        return "ykwt500"

    @property
    def InstrumentFirmwareRevision(self):
        return "unknown"

    @property
    def Identifier(self):
        return self.parent.interface.vi_query("*IDN?")

    @property
    def SupportedInstrumentModels(self):
        return "ykwt500"

    @property
    def SpecificationMajorVersion(self):
        raise NotImplementedError

    @property
    def SpecificationMinorVersion(self):
        raise NotImplementedError

    @property
    def GroupCapabilities(self):
        return "unknown"
