from __future__ import annotations

import pyvisa

from . import ke2450_const


class ke2450_DevUtils:
    def __init__(self, parent):
        self._parent = parent
        """:type : pverify.drivers.SMU.ke2450.ke2450.ke2450"""

        from .IIviDriverOperation import IIviDriverOperation

        self.DriverOperation = IIviDriverOperation(self)

        from .ke2450_LowLevel import ke2450_LowLevel

        self.LowLevel = ke2450_LowLevel(self)

        self.Enums = ke2450_const

        self._visa_rm = pyvisa.ResourceManager()
        self._visa_mbr = None
        """:type : MessageBasedResource"""

    @property
    def vi(self):
        """
        The instrument IO session
        """
        return self._parent._vi

    @property
    def dllwrap(self):
        """
        The Python dll wrapper module
        """
        return self._parent._dllwrap

    @property
    def visa_mbr(self):
        """
        The message based visa resource
        """
        return self._visa_mbr
