from __future__ import annotations

from .AgInfiniium import *


class IIviScopeReferenceLevel:
    def __init__(self, root):
        self.root = root
        """:type : pverify.drivers.Scope.AgInfiniium.IIviScope.IIviScope"""

    @property
    def High(self):
        return self.root.dllwrap.GetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 607)

    @High.setter
    def High(self, value):
        """
        The high reference for waveform measurements. It is a percentage of the
        difference between the Voltage High and Voltage Low. Voltage High and Voltage
        Low may be calculated using either the min/max or histogram methods.
        """
        self.root.dllwrap.SetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 607, value)

    @property
    def Low(self):
        return self.root.dllwrap.GetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 608)

    @Low.setter
    def Low(self, value):
        """
        The low reference for waveform measurements. It is a percentage of the
        difference between the Voltage High and Voltage Low. Voltage High and Voltage
        Low may be calculated using either the min/max or histogram methods.
        """
        self.root.dllwrap.SetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 608, value)

    @property
    def Mid(self):
        return self.root.dllwrap.GetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 609)

    @Mid.setter
    def Mid(self, value):
        """
        The mid reference for waveform measurements. It is a percentage of the
        difference between the Voltage High and Voltage Low. Voltage High and Voltage
        Low may be calculated using either the min/max or histogram methods.
        """
        self.root.dllwrap.SetAttributeViReal64(None, IVI_CLASS_PUBLIC_ATTR_BASE + 609, value)

    def Configure(self, Low, Mid, High):
        """
        Configures the reference levels for waveform measurements, low, mid, and high.

        :param Low: Measurement low reference. This value sets the Meas Low Ref property
        :type Low: float
        :param Mid: Measurement mid reference. This value sets the Meas Mid Ref property.
        :type Mid: float
        :param High: Measurement high reference. This value sets the Meas High Ref property.
        :type High: float
        """
        self.root.dllwrap.ConfigureRefLevels(Low, Mid, High)
