from __future__ import annotations

from .tkdpo4k import *


class IIviScopeTriggerAcLine:
    def __init__(self, root):
        self.root = root

    @property
    def Slope(self):
        return self.root.dllwrap.GetAttributeViInt32(None, IVI_CLASS_PUBLIC_ATTR_BASE + 701)

    @Slope.setter
    def Slope(self, value):
        """
        The slope of the zero crossing upon which the scope triggers.
        """
        self.root.dllwrap.SetAttributeViInt32(None, IVI_CLASS_PUBLIC_ATTR_BASE + 701, value)
