from __future__ import annotations

from .tkmso5x import *


class IIviScopeTriggerEdge:
    def __init__(self, root):
        self.root = root
        """:type : pverify.drivers.Scope.tkmso5x.IIviScope.IIviScope"""

    @property
    def Slope(self):
        return self.root.dllwrap.GetAttributeViInt32(None, IVI_CLASS_PUBLIC_ATTR_BASE + 18)

    @Slope.setter
    def Slope(self, value):
        """
        The slope, a rising or a falling edge, that triggers the oscilloscope.
        """
        self.root.dllwrap.SetAttributeViInt32(None, IVI_CLASS_PUBLIC_ATTR_BASE + 18, value)

    def Configure(self, Source, Level, Slope):
        """
        Configures the conidtions for edge trigger. An edge trigger occurs when the
        trigger source signal passes through the trigger level with the specified slope.

        :param Source: Specifies the trigger source. This value sets the Trigger Source property.
        :type Source: str
        :param Level: Specifies the trigger level. This value sets the Trigger Level property.
        :type Level: float
        :param Slope: Specifies the trigger slope. This value sets the Trigger Slope property.
        :type Slope: IviScopeTriggerSlopeEnum
        """
        self.root.dllwrap.ConfigureEdgeTriggerSource(Source, Level, Slope)
