from __future__ import annotations

from pverify.drivers.Scope.ykdlm4000 import IIviScopeBase


class IIviScopeTriggerRunt(IIviScopeBase.IIviScopeBase):
    def __init__(self, interface):
        IIviScopeBase.IIviScopeBase.__init__(self, interface)

    # Read-write dependent properties
    @property
    def polarity(self):
        """
        The polarity of the runt that triggers the oscilloscope.
        """
        raise NotImplementedError

    @polarity.setter
    def polarity(self, value):
        """
        The polarity of the runt that triggers the oscilloscope.
        """
        raise NotImplementedError

    @property
    def ThresholdHigh(self):
        """
        The high threshold the oscilloscope uses for runt triggering. The
        units are volts.
        """
        raise NotImplementedError

    @ThresholdHigh.setter
    def ThresholdHigh(self, value):
        """
        The high threshold the oscilloscope uses for runt triggering. The
        units are volts.
        """
        raise NotImplementedError

    @property
    def ThresholdLow(self):
        """
        The low threshold the oscilloscope uses for runt triggering. The units
        are volts.
        """
        raise NotImplementedError

    @ThresholdLow.setter
    def ThresholdLow(self, value):
        """
        The low threshold the oscilloscope uses for runt triggering. The units
        are volts.
        """
        raise NotImplementedError

    # Methods
    def Configure(self, Source, ThresholdLow, ThresholdHigh, polarity):
        """
        Configures the runt trigger Source, ThresholdLow, ThresholdHigh, and
        Polarity. A runt trigger occurs when the trigger signal crosses one of
        the runt thresholds twice without crossing the other runt threshold.

        :param Source:
            Specifies the trigger source. This value sets the Trigger Source
            property.

        :param ThresholdLow:
            Sets the runt triggering low threshold in volts.

        :param ThresholdHigh:
            Sets the runt triggering high threshold in volts.

        :param polarity:
            Sets the runt polarity.

        """
        raise NotImplementedError
