from __future__ import annotations

from enum import IntEnum


def key_by_value(enum, value: int | str):
    for _, v in enum.__members__.items():
        if v.value == value:
            return v
    msg = f"Cannot find value '{value}' in members of enum '{enum}'!"
    raise LookupError(msg)


class StecEnums:
    class TYPES(IntEnum):
        TSR2252 = 0

    class MODE(IntEnum):
        AIR_CTRL = 0
        DUT_CTRL = 1

    class HEAD_POS(IntEnum):
        UP = 0
        DOWN = 1

    class SENSOR(IntEnum):
        NONE = 0
        T = 1
        K = 2
