from __future__ import annotations

import logging
import sys


class Logger(logging.Logger):
    def __init__(self):
        super().__init__(self)
        self.my_logger = logging.getLogger("my_logger")
        self.my_logger.setLevel(logging.NOTSET)

        # setup handler for stdout
        self.add_stream(sys.stdout)

    def add_stream(self, the_stream):
        hdlr = logging.StreamHandler(the_stream)
        hdlr.setLevel(logging.NOTSET)
        # formatter = logging.Formatter('%(asctime)s - %(name)s - %(levelname)s - %(message)s')
        formatter = logging.Formatter("%(asctime)s - %(levelname)s - %(message)s")
        hdlr.setFormatter(formatter)
        self.addHandler(hdlr)


logger = Logger()


def main():
    log = Logger()
    log.warning("print warning")
    log.info("print info")


if __name__ == "__main__":
    main()
