from __future__ import annotations

from monocle_telemetry.api.feature import track_feature as _track_feature

from srd.version import __version_tuple__


def track_feature(feature):
    _track_feature(
        project="pyveco",
        tool="ifx-srd",
        version=".".join(map(str, __version_tuple__[:3])),
        feature=f"srd_{feature}",
    )


class FEATURES:
    srd_runsim_simetrix = "runsim_simetrix"
    srd_runsim_simplis = "runsim_simplis"
    srd_runsim_titan = "runsim_titan"
    srd_runsim_amsdesigner = "runsim_amsdesigner"
