from __future__ import annotations

from monocle_telemetry.api.feature import track_feature

from ..version import __version_tuple__  # noqa: TID252

package_name = __package__.split(".")[0]
dist_name = package_name if package_name.startswith("ifx-") else f"ifx-{package_name}"


def track(feature: str, misc: str = ""):
    track_feature(
        project="pyveco",
        tool=dist_name,
        version=".".join(map(str, __version_tuple__[:3])),
        feature=feature,
        misc=misc,
    )
